    alignItems: 'center',
    justifyContent: 'flex-start',
    margin: `${theme.spacing.unit}px 0`,
  },
  button: {
    margin: theme.spacing.unit,
  },

});

const mapStateToProps = state => {
  return {
    beacon: state.beacon,
    vitals: state.vitals,
    info: state.info,
  };
};

