import React, { Component } from 'react';
import AppBar from '@material-ui/core/AppBar';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Toolbar from '@material-ui/core/Toolbar';
import { withStyles } from '@material-ui/core/styles';
import { connect } from "react-redux";
import { addStaff, confirmType, fetchMaps, updateStaff, updateStaffCount, fetchStaffCount, editPatient, removeStaff, signOutUser, assignBeacon } from "./actions/index";
import Card from '@material-ui/core/Card';
import CircularProgress from '@material-ui/core/CircularProgress';
import TemporaryDrawer from './TemporaryDrawer';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import AddIcon from '@material-ui/icons/Add';
import AccountCircle from '@material-ui/icons/AccountCircle';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import FormControl from '@material-ui/core/FormControl';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import classNames from 'classnames';
import InputAdornment from '@material-ui/core/InputAdornment';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormLabel from '@material-ui/core/FormLabel';
import BeaconTable from './BeaconTable';
import Select from 'react-select';
import axios from 'axios';
import NoSsr from '@material-ui/core/NoSsr';
import Paper from '@material-ui/core/Paper';
import Chip from '@material-ui/core/Chip';
import CancelIcon from '@material-ui/icons/Cancel';
import ClickAwayListener from '@material-ui/core/ClickAwayListener';
import LinearProgress from '@material-ui/core/LinearProgress';

const graphqlUrlHTTPS = 'http://137.132.165.139:3000/graphql';


const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  root: {
    flexGrow: 1,
  },
  grow: {
    flexGrow: 1,
  },
  card: {
    minWidth: 275,
    padding: '2rem'
  },
  textField: {
    // marginLeft: theme.spacing.unit,
    // marginRight: theme.spacing.unit,
  },
  borderBox: {
    border: '1px solid',
    padding: '2rem',
    borderRadius: '16px',
  },
  menu: {
    width: 200,
  },
  menuButton: {
    marginLeft: -12,
    marginRight: 20,
    opacity : 0.4,
    color: 'black',
  },
  align: {
    padding: `${theme.spacing.unit * 2}px ${theme.spacing.unit * 3}px ${theme.spacing.unit * 3}px`,
  },
  input: {
    display: 'flex',
    padding: 0,
  },
  valueContainer: {
    display: 'flex',
    flexWrap: 'wrap',
    flex: 1,
    alignItems: 'center',
    overflow: 'hidden',
    padding: '18.5px 14px',
  },
  noOptionsMessage: {
   padding: `${theme.spacing.unit}px ${theme.spacing.unit * 2}px`,
 },
 singleValue: {
    fontSize: 16,
  },
  placeholder: {
    position: 'absolute',
    left: 2,
    fontSize: 16,
    padding: '18.5px 14px',
  },
  paper: {
    position: 'absolute',
    zIndex: 1,
    marginTop: theme.spacing.unit,
    left: 0,
    right: 0,
  },
});

const mapStateToProps = state => {
  return {
    beacon: state.beacon,
    maps: state.maps,
    edit: state.edit,
    isAuthenticating: state.isAuthenticating,
    authenticated: state.authenticated,
    staffCounter: state.staffCounter
   };
};

const mapDispatchToProps = dispatch => {
  return {
    confirmType: type => dispatch(confirmType(type)),
    addStaff: staff => dispatch(addStaff(staff)),
    fetchMaps: maps => dispatch(fetchMaps(maps)),
    assignBeacon: beacon => dispatch(assignBeacon(beacon)),
    removeStaff: staff => dispatch(removeStaff(staff)),
    editPatient: patient => dispatch(editPatient(patient)),
    signOutUser: credentials => dispatch(signOutUser(credentials)),
    fetchStaffCount: count => dispatch(fetchStaffCount(count)),
    updateStaffCount: count => dispatch(updateStaffCount(count)),
    updateStaff: staff => dispatch(updateStaff(staff)),
  };
};

function NoOptionsMessage(props) {
  return (
    <Typography
      color="textSecondary"
      className={props.selectProps.classes.noOptionsMessage}
      {...props.innerProps}
    >
      {props.children}
    </Typography>
  );
}

function inputComponent({ inputRef, ...props }) {
  return <div ref={inputRef} {...props} />;
}

function Control(props) {
  return (
    <TextField
      fullWidth
      variant="outlined"
      required={true}
      error={props.selectProps.error}
      helperText={ props.selectProps.error ? "This field is required" : null}
      InputProps={{
        inputComponent,
        inputProps: {
          className: props.selectProps.classes.input,
          inputRef: props.innerRef,
          children: props.children,
          ...props.innerProps,
        },
      }}
      {...props.selectProps.textFieldProps}
    />
  );
}

function Option(props) {
  return (
    <MenuItem
      buttonRef={props.innerRef}
      selected={props.isFocused}
      component="div"
      style={{
        fontWeight: props.isSelected ? 500 : 400,
      }}
      {...props.innerProps}
    >
      {props.children}
    </MenuItem>
  );
}

function Placeholder(props) {
  return (
    <Typography
      color="textSecondary"
      className={props.selectProps.classes.placeholder}
      {...props.innerProps}
    >
      {props.children}
    </Typography>
  );
}

function SingleValue(props) {
  return (
    <Typography className={props.selectProps.classes.singleValue} {...props.innerProps}>
      {props.children}
    </Typography>
  );
}

function ValueContainer(props) {
  return <div className={props.selectProps.classes.valueContainer}>{props.children}</div>;
}

function MultiValue(props) {
  return (
    <Chip
      tabIndex={-1}
      label={props.children}
      className={classNames(props.selectProps.classes.chip, {
        [props.selectProps.classes.chipFocused]: props.isFocused,
      })}
      onDelete={props.removeProps.onClick}
      deleteIcon={<CancelIcon {...props.removeProps} />}
    />
  );
}

function SubMenu(props) {
  return (
    <Paper square className={props.selectProps.classes.paper} {...props.innerProps}>
      {props.children}
    </Paper>
  );
}

const components = {
  Control,
  SubMenu,
  MultiValue,
  NoOptionsMessage,
  Option,
  Placeholder,
  SingleValue,
  ValueContainer,
};

class AddStaff extends Component {
  constructor(props) {
    super(props);

    this.state = {
      anchorEl: null,
      auth: true,
      name: "",
      age: "",
      race: "",
      gender: "",
      ward: "",
      type: "Staff",
      nameError: false,
      wardError: false,
      beacon: this.props.beacon,
      editSuccess: false,
    };
  }

  componentWillUnmount() {
    if(this.state.editSuccess == false) {
      this.props.editPatient('')
    }
  }

  componentDidMount() {
    this.fetch();
    this.props.fetchStaffCount()
    if(this.props.edit != "" && this.props.edit != null) {
      let edit = this.props.edit;
      this.setState({
        name: edit.name,
        age: edit.age,
        race: edit.race,
        gender: edit.gender,
        ward: {value: edit.ward, label: edit.ward},
        edit: true,
      })
    } else {
      this.flushData()
    }
  }

  componentDidUpdate(prevProps, prevState) {
    if(this.state.edit == true && this.props.edit == "") {
      this.flushData()

    }

    if(this.props.isAuthenticating == false && this.props.authenticated == false) {
      this.props.history.push('/');
    }
  }

  flushData = () => {
    this.setState({
      name: "",
      age: "",
      race: "",
      gender: "",
      ward: "",
      edit: false,
    })

    let chosen = ""
    this.props.assignBeacon( chosen );
  }

  handleLogOut = () => {
    this.handleClose()
    this.props.signOutUser()
  }

  createOptions = (maps) => {

    let options = maps.map((map) => ({
      value: map.id,
      label: map.id,
    }))

    this.setState({options})
  }

  fetch = (dispatch) => {
    let query = `
      query {
        maps {
          id
          scale
          coordinates
          imageURL
          navMesh
          navPath
          pois
        }
      }
    `
    this.request({query})
    .then (data => {
      this.props.fetchMaps(data.data.data.maps);
      this.createOptions(data.data.data.maps);
    })
  }

  request = ({query, variables}) => {
    let promise = axios({
      method:   'post',
      url:      `${graphqlUrlHTTPS}`,
      headers:  {'Content-Type': 'application/json'},
      data:     JSON.stringify({query, variables})
    })
    return promise;
  }

  toggleDrawer = () => {
    this.setState({ drawer: true});
  }

  toggleCloseDrawer = () => {
    this.setState({drawer: false})
  }

  handleMenu = event => {
    this.setState({ drawer: false });
    this.setState({ anchorEl: event.currentTarget });
  };

  handleClose = () => {
    this.setState({ anchorEl: null });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    const { type } = this.state;
    this.handleClose()
    let errorCheck = false
    if(this.state.name == "") {
      this.setState({nameError: true})
      errorCheck = true
    }

    if(this.state.ward.value == null) {
      this.setState({wardError: true})
      errorCheck = true
    }

    if(errorCheck)
      return
    let staff = {
      name: this.state.name,
      age: this.state.age,
      race: this.state.race,
      gender: this.state.gender,
      ward: this.state.ward.value,
      beacon: this.props.beacon,
      highlight: false,
     }
    let confirm = {
      name: this.state.name,
      age: this.state.age,
      race: this.state.race,
      gender: this.state.gender,
      ward: this.state.ward.value,
      beacon: this.props.beacon,
      type: type}

      if(this.state.edit) {
        staff.id = this.props.edit.id
        this.props.updateStaff(staff)

        this.setState({editSuccess: true})
      } else {
        this.props.updateStaffCount(this.props.staffCounter.count)
        this.props.addStaff(staff);
      }
    this.props.confirmType({ confirm });

    let chosenBeacon = "None"
    this.props.assignBeacon( chosenBeacon );
    this.props.history.push('/addConfirmation');

  }

  handleLoading = () => {
    this.setState(state => ({
      loading: !state.loading,
    }))
  }

  removeStaff = () => {
    this.props.removeStaff(this.props.edit)
    this.props.history.push('/locationTracking')
  }

  handleChange = prop => event => {
    if(prop == "ward")
      this.setState({ [prop]: event });
    else
      this.setState({ [prop]: event.target.value });
  };

  cancel = () => {
    if(this.state.edit) {
      this.props.history.push('/locationTracking')
    } else
      this.props.history.push('/home');
  }

  render() {

    const { classes, theme } = this.props;
    const { anchorEl, auth } = this.state;
    const open = Boolean(anchorEl)
    const races = ['Chinese', 'Malay', 'Indian', 'Others'];

    const selectStyles = {
      input: base => ({
        ...base,
        color: theme.palette.text.primary,
        '& input': {
          font: 'inherit',
        },
      }),
    };
  if(this.props.isAuthenticating) {
    return <LinearProgress />
  } else
    return (
      <div className={classes.root}>
        <AppBar position="static" style={{boxShadow: "none", backgroundColor: "white"}}>
          <Toolbar>
            <IconButton className={classes.menuButton} color="inherit" aria-label="Menu" onClick={this.toggleDrawer}>
              <MenuIcon />
            </IconButton>
            <Typography variant="title" className={classes.grow} style={{color: "black"}}>
              {this.state.edit ? 'Edit': 'Add'} Staff
            </Typography>
            {auth && (
                <div>
                  <IconButton
                    aria-owns={open ? 'menu-appbar' : null}
                    aria-haspopup="true"
                    onClick={this.handleMenu}
                    color="inherit"
                  >
                    <AccountCircle style={{color: "black"}} />
                  </IconButton>
                  <Menu
                    id="menu-appbar"
                    anchorEl={anchorEl}
                    anchorOrigin={{
                      vertical: 'top',
                      horizontal: 'right',
                    }}
                    transformOrigin={{
                      vertical: 'top',
                      horizontal: 'right',
                    }}
                    open={open}
                    onClose={this.handleClose}
                  >
                    <MenuItem onClick={this.handleLogOut}>Logout</MenuItem>
                  </Menu>
                </div>
              )}
          </Toolbar>
        </AppBar>
        <ClickAwayListener onClickAway={this.toggleCloseDrawer}>
          <TemporaryDrawer toggle={this.state.drawer} />
        </ClickAwayListener>
        <form className={classes.align}>
          <Grid container
            direction="column"
            spacing={0}
            alignItems="center"
            style={{ minHeight: '75vh'}}>
            <Grid item xs={12}>
              <div className={classes.layout}>
                <Grid container>
                  <Grid item xs={6}>
                    <Typography variant="h4" gutterBottom>Staff</Typography>
                  </Grid>
                  <Grid item xs={6} style={{margin: 'auto'}}>
                    {this.state.edit ? <Button variant="outlined" onClick={this.removeStaff} style={{float: 'right'}}> Delete </Button> : null}
                  </Grid>
                </Grid>

                <FormControl margin="normal" required fullWidth>
                  <TextField
                    label="Name"
                    placeholder="Name"
                    autoComplete="name"
                    variant="outlined"
                    required = {true}
                    value={this.state.name}
                    error={this.state.nameError}
                    helperText={this.state.nameError ? "This field is required" : null}
                    fullWidth
                    className={classes.textField}
                    onChange = {(e) => this.setState({name:e.target.value})}
                  />
                </FormControl>
              </div>
              <Grid container
                direction="row"
                spacing={24}
                alignItems="center"
              >
                <Grid item xs={6}>
                  <FormControl margin="normal" required>
                    <TextField
                    id="outlined-adornment-age"
                    className={classNames(classes.margin, classes.textField)}
                    variant="outlined"
                    label="Age"
                    value={this.state.age}
                    onChange={this.handleChange('age')}
                    InputProps={{
                      endAdornment: <InputAdornment position="end">Years Old</InputAdornment>,
                    }}
                    />
                  </FormControl>
                </Grid>
                <Grid item xs={6} style={{textAlign: 'center'}}>
                  <FormControl component="fieldset" margin="normal" className={classes.formControl}>
                    <FormLabel component="legend">Gender</FormLabel>
                    <RadioGroup
                    aria-label="Gender"
                    name="gender1"
                    row ={true}
                    className={classNames(classes.margin, classes.textField, classes.group)}
                    value={this.state.gender}
                    onChange={this.handleChange('gender')}
                    >
                      <FormControlLabel value="male" control={<Radio />} label="Male" />
                      <FormControlLabel value="female" control={<Radio />} label="Female" />
                    </RadioGroup>
                  </FormControl>
                </Grid>
              </Grid>
              <Grid container
                direction="row"
                spacing={0}
                alignItems="center"
                >
                <Grid item xs={12}>
                  <FormControl required fullWidth>
                    <TextField
                    id="outlined-select-race"
                    select
                    label="Race"
                    className={classes.textField}
                    value={this.state.race}
                    onChange={this.handleChange('race')}
                    SelectProps={{
                      MenuProps: {
                        className: classes.menu,
                      },
                    }}
                    margin="normal"
                    variant="outlined"
                    >
                    {races.map(race => (
                      <MenuItem key={race} value={race}>
                        {race}
                      </MenuItem>
                    ))}
                    </TextField>
                  </FormControl>
                  <FormControl margin="normal" required fullWidth>
                    <NoSsr>
                      <Select
                      classes={classes}
                      styles={selectStyles}
                      options={this.state.options}
                      components={components}
                      value={this.state.ward}
                      onChange={this.handleChange('ward')}
                      placeholder="Ward*"
                      isClearable
                      error={this.state.wardError}
                      />
                    </NoSsr>
                  </FormControl>
                  <BeaconTable />
                </Grid>
              </Grid>
              <br/>
              <div style={{ textAlign: 'center', paddingTop: '1rem'}}>
                <Button variant="contained" color="primary" onClick={(event) => this.handleSubmit(event)}>
                  Submit
                </Button>
                <Button style={{marginLeft: '3em'}} onClick={this.cancel}>
                  Cancel
                </Button>
              </div>
            </Grid>
          </Grid>
        </form>
      </div>
    )
  }
}

const ConnectedAddStaff = connect(mapStateToProps, mapDispatchToProps)(AddStaff);

export default withStyles(styles,  {withTheme: true})(ConnectedAddStaff);
